        //method to test for voter fraud (check for repeated ID numbers)
        public static boolean repeatID (String voterID){
            int numberOfIDRepeats= 0;
            for (int i = 0; i < electionDataByVoter.length; i++) {
                if (voterID.equals(electionDataByVoter[i].substring(0, 6))) {
                    numberOfIDRepeats++;
                }
            }
            if (numberOfIDRepeats >= 2)
                return true;
                else
                    return false;
        }

        //method to count votes by ID (1, 2, 3, 4)
        public static void tallyVotes ( int presVote, int VPVote, int secVote, int treasVote){
            if (presVote== 1){
                pres1++;
            } else if (presVote== 2){
                pres2++;
            } else if (presVote== 3){
                pres3++;
            } else if (presVote== 4){
                pres4++;
            }

            if (VPVote== 1){
                VP1++;
            } else if (VPVote== 2){
                VP2++;
            } else if (VPVote== 3){
                VP3++;
            } else if (VPVote== 4){
                VP4++;
            }

            if (secVote== 1){
                sec1++;
            } else if (secVote== 2){
                sec2++;
            } else if (secVote== 3){
                sec3++;
            } else if (secVote== 4){
                sec4++;
            }

            if (treasVote== 1){
                treas1++;
            } else if (treasVote== 2){
                treas2++;
            } else if (treasVote== 3){
                treas3++;
            } else if (treasVote== 4){
                treas4++;
            }
        }
